    private void subtrairMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_subtrairMouseClicked
        int valorS1 = Integer.parseInt(valorSub1.getText());
        int valorS2 = Integer.parseInt(valorSub2.getText());
        int subtrair2 = valorS1-valorS2;

        resultado2.setText(String.valueOf(subtrair2));// TODO add your handling code here:
    }//GEN-LAST:event_subtrairMouseClicked

    private void multiplicarMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_multiplicarMouseClicked
        int valorM1 = Integer.parseInt(valorMult1.getText());
        int valorM2 = Integer.parseInt(valorMult2.getText());
        int multiplicar2 = valorM1 * valorM2;
        resultado3.setText(String.valueOf(multiplicar2));// TODO add your handling code here:
    }//GEN-LAST:event_multiplicarMouseClicked

    private void divisaoMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_divisaoMouseClicked
        int valorD1 = Integer.parseInt(valorDiv1.getText());
        int valorD2 = Integer.parseInt(valorDiv2.getText());

        int divisao2 = valorD1 / valorD2;

        resultado4.setText(String.valueOf(divisao2));// TODO add your handling code here:
    }//GEN-LAST:event_divisaoMouseClicked

