    private void operacoesItemStateChanged(java.awt.event.ItemEvent evt) {//GEN-FIRST:event_operacoesItemStateChanged
        if(operacoes.getSelectedItem().equals("Soma")){
        int num1 =Integer.parseInt(valor1.getText());
        int num2 = Integer.parseInt(valor2.getText());
        int soma = num1+num2;
        resultado5.setText(String.valueOf(soma));
              
        } 
        else if(operacoes.getSelectedItem().equals("Subtrair")){
        int num1 = Integer.parseInt(valor1.getText());
        int num2 = Integer.parseInt(valor2.getText());
        
        int sub = num1-num2;
        
        resultado5.setText(String.valueOf(sub));
        }
        else if(operacoes.getSelectedItem().equals("Multiplicar")){
        int num1 = Integer.parseInt(valor1.getText());
        int num2 = Integer.parseInt(valor2.getText());
        
        int mult = num1*num2;
        
        resultado5.setText(String.valueOf(mult));
        }// ODO add your handling code here:
        else if(operacoes.getSelectedItem().equals("Divisao")){
        int num1 =Integer.parseInt(valor1.getText());
        int num2 = Integer.parseInt(valor2.getText());
        
        int divisao = num1/num2;
        
        resultado5.setText(String.valueOf(divisao));
        
        
        }
    }//GEN-LAST:event_operacoesItemStateChanged
        
    
    
